    { name: "Restaurant"},
    { name: "Bookstore"},
    { name: "Household"},
    { name: Phonedata},
    { name: Flowershop},
];

const handleChange = (e) => {
    e.preventDefault();
    setSearchInput(e.target.value);

    if (searchInput.length > 0){
        categoriesOfGift.filter((category) => {
            return category.name.match(searchInput);
