const Home = () => {
   const [usersNum, setUsersNum] = useState(null);

   useEffect(() => {
      axios
         .get(`${process.env.REACT_APP_BE_URL}`)
         .then((res) => {
            setUsersNum(res.data);
         })
         .catch((err) => {
            console.log(err.request.response);
